// Get input from Webhook or the previous node
const input = $input.first().json;

// Check for required technical IDs
if (!input.user_id || !input.batch_id) {
  throw new Error('Missing required fields: user_id and batch_id');
}

// Extract term and location logic
let rawSearch = input.term || ""; 
let term = rawSearch;
let location = input.location || "";

// Split logic if phrase contains "in" or "at"
const splitMatch = rawSearch.match(/(.+)\s+(?:in|at)\s+(.+)/i);

if (splitMatch) {
    term = splitMatch[1].trim();
    location = splitMatch[2].trim();
}

return [{
  json: {
    term: term,
    location: location,
    search_query: `${term} ${location}`.trim(),
    batch_id: input.batch_id,
    user_id: input.user_id,
    started_at: new Date().toISOString()
  }
}];

const runId = $input.first().json.data.id;
const originalInput = $('Input Validation1').item.json;
const apifyToken = 'REDACTED_BY_REGEX'; 
const maxRetries = 20; 
const pollInterval = 5000; 

for (let i = 0; i < maxRetries; i++) {
  const response = await this.helpers.httpRequest({
    method: 'GET',
    url: `https://api.apify.com/v2/actor-runs/${runId}?token=${apifyToken}`
  });

  const status = response.data.status;

  if (status === 'SUCCEEDED') {
    return [{
      json: {
        run_id: runId,
        dataset_id: response.data.defaultDatasetId,
        status: 'completed',
        ...originalInput
      }
    }];
  }

  if (status === 'FAILED' || status === 'ABORTED' || status === 'TIMED-OUT') {
    throw new Error(`Apify Run failed with status: ${status}`);
  }

  await new Promise(resolve => setTimeout(resolve, pollInterval));
}

throw new Error('Apify timed out.');

