// 1. Get the raw text content from the previous OpenAI node
const response = $input.first().json;
let content = response.choices[0].message.content;

// 2. Remove the Markdown code blocks (```json ... ```)
// This regex replaces ```json, ```, and newlines with nothing
const cleanContent = content.replace(/^```json\s*|\s*```$/g, '').trim();

let parsedData;
try {
  // 3. Convert the clean string into a real JSON Object
  parsedData = JSON.parse(cleanContent);
} catch (error) {
  // Fallback if parsing fails
  parsedData = { error: "Could not parse JSON", raw: content };
}

// 4. Return the clean data
return { json: parsedData };

