const response = $input.first().json;
const config = $('Set Configuration').first().json;
let topics = [];
try {
  let content = response.choices[0].message.content;
  content = content.replace(/```json\n?/g, '').replace(/```/g, '').trim();
  const parsed = JSON.parse(content);
  topics = parsed.trending_topics || [];
} catch (e) {
  console.error('Parse error:', e);
  return [];
}
const validTopics = topics.filter(t => t.topic && t.primary_keyword && t.sources && t.sources.length >= 2).map((t, i) => ({...t, topic_score: 70, topic_index: i, workflow_config: config})).slice(0, config.articles_per_run);
return validTopics.map(t => ({json: t}));

