// 1. Get the Clean Article from the previous node
const article = $input.first().json.final_article;

// 2. Get the Topic (Try to get it from 'Merge Data', otherwise use a default)
let topic = "AI Article";
try {
  topic = $('Merge Data').item.json.topic;
} catch(e) {
  // If Merge Data isn't found, try to guess the topic from the first line
  const match = article.match(/^#\s*(.+)$/m);
  if (match) topic = match[1];
}

// 3. Create a URL Slug (e.g. "AI Models 2026" -> "ai-models-2026")
const slug = topic.toLowerCase()
  .replace(/[^a-z0-9]+/g, '-') // Replace symbols with -
  .replace(/(^-|-$)/g, '');    // Remove extra - at start/end

// 4. Create a short Excerpt (First 150 chars, no symbols)
const excerpt = article.substring(0, 150).replace(/#|\*/g, '') + "...";

// 5. Extract the Title (The text after the first #)
const titleMatch = article.match(/^#\s*(.+)$/m);
const title = titleMatch ? titleMatch[1] : topic;

// 6. Remove the H1 Title from the body content (so it doesn't appear twice)
const bodyWithoutH1 = article.replace(/^#\s*.+$/m, '').trim();

return {
  json: {
    wp_title: title,
    wp_slug: slug,
    wp_excerpt: excerpt,
    wp_content: bodyWithoutH1
  }
};

