let enriched = [];
for (const item of items) {
  let rawOutput = item.json.output || item.json.choices?.[0]?.message?.content || "";

  // üî• Clean markdown fences like ```json ... ```
  rawOutput = rawOutput.replace(/```json/gi, "").replace(/```/g, "").trim();

  let output;
  try {
    output = JSON.parse(rawOutput);
  } catch (e) {
    output = {
      short_title: null,
      bullets: [],
      seo_keywords: [],
      image_alt_text: null,
      long_description: null
    };
  }

  enriched.push({
    json: {
      product_id: output.id || null,
      short_title: output.short_title,
      bullets: output.bullets,
      seo_keywords: output.seo_keywords,
      image_alt_text: output.image_alt_text,
      long_description: output.long_description,
      audit_passed: false,
      audit_reasons: []
    }
  });
}
return enriched;

// Input: items from HTTP Request (assumes items[0].json is array or HTTP Request returns array as top-level)
const input = items[0].json;

// Handle two shapes n8n may present:
// - If HTTP Request returned an array, n8n usually maps each array element to an item (items already many).
// - If items come in a single item with json being array, we iterate.
let products = [];

// Case 1: items is many, each .json is a product
if (Array.isArray(items) && items.length > 1 && items[0].json && items[0].json.title) {
  products = items.map(it => it.json);
} else if (Array.isArray(input)) {
  // Case 2: single item where json is an array
  products = input;
} else if (input.products && Array.isArray(input.products)) {
  products = input.products;
} else {
  // Fallback ‚Äî try to coerce
  products = Array.isArray(input) ? input : [input];
}

// Normalization helper
function normalizeProduct(p) {
  return {
    product_id: p.id != null ? String(p.id) : (p.sku ? String(p.sku) : null),
    title: p.title || p.name || '',
    category: p.category || p.department || '',
    price: (p.price != null) ? Number(p.price) : null,
    currency: p.currency || 'USD',
    description_raw: p.description || p.long_description || '',
    images: Array.isArray(p.images) ? p.images.join(',') : (p.image ? String(p.image) : ''),
    inventory: (p.rating && p.rating.count != null) ? Number(p.rating.count) : (p.stock != null ? Number(p.stock) : null),
    source_origin: 'FakeStoreAPI',
    normalized_at: new Date().toISOString(),
    ai_meta_description: '',
    audit_passed: false,
    audit_reasons: []
  };
}

const out = products.map(p => ({ json: normalizeProduct(p) }));
return out;
