// Clean the URL to remove tracking parameters and whitespace
for (const item of $input.all()) {
  let url = item.json.website_url || "";
  if (url) {
    // Remove everything after '?' (UTM tags, etc)
    url = url.split('?')[0]; 
    // Remove trailing slashes and whitespace
    url = url.trim().replace(/\/+$/, ""); 
  }
  item.json.clean_url = url;
}
return $input.all();

const items = $input.all();
return items.map((item, index) => {
  let originalLead = null;
  try {
    const scraped = $("Scarp from web site1").all();
    if (scraped[index]) originalLead = scraped[index].json;
  } catch (e) {
    try {
      const manual = $("Set data1").all();
      if (manual[index]) originalLead = manual[index].json;
    } catch (e2) {}
  }

  const analysis = JSON.parse(item.json.output.replace(/```json/g, '').replace(/```/g, '').trim());

  return {
    json: {
      id: originalLead ? originalLead.id : "missing_id", // THIS IS THE CRITICAL LINE
      business_name: originalLead ? originalLead.business_name : "Unknown",
      fit_score: analysis.fit_score,
      summary: analysis.summary,
      is_high_ticket: analysis.is_high_ticket
    }
  };
});

// 1. Get the drafts from the current input
const drafts = $input.all();

return drafts.map((item, index) => {
  let leadId = "missing_id";
  let bizName = "Unknown";
  let leadScore = 0;
  let leadSummary = "No reasoning available";
  let emailList = [];

  // 2. Try to find the original lead data from ANY available previous node
  try {
    // Attempt to pull from Clean AI Output2 (The Analysis stage)
    const analysisData = $("Clean AI Output2").all()[index].json;
    leadId = analysisData.id || leadId;
    bizName = analysisData.business_name || analysisData.name || bizName;
    leadScore = analysisData.fit_score || 0;
    leadSummary = analysisData.summary || leadSummary;
  } catch (e) {
    try {
      // Fallback: Attempt to pull directly from the Loop node
      const loopData = $("Loop Over Items3").all()[index].json;
      leadId = loopData.id || leadId;
      bizName = loopData.business_name || bizName;
    } catch (e2) {}
  }

  // 3. Try to recover emails from the scraper path
  try {
    const scraped = $("Scarp from web site1").all();
    const match = scraped.find(s => s.json.id === leadId);
    emailList = match ? (match.json.all_scraped_emails || []) : [];
  } catch (e) {}

  try {
    // 4. Parse the AI Email output
    let rawAiText = item.json.output || "";
    let cleanedJson = rawAiText.replace(/```json/g, '').replace(/```/g, '').trim();
    cleanedJson = cleanedJson.replace(/\\"/g, '"'); // Fix double-escaping
    const emailData = JSON.parse(cleanedJson);

    return {
      json: {
        id: leadId,
        all_emails: emailList,
        business_name: bizName,
        fit_score: leadScore,
        score_reasoning: leadSummary,
        raw_email_full: rawAiText,
        email_subject: emailData.subject,
        email_body: emailData.body,
        status: 'ready_for_outreach'
      }
    };
  } catch (error) {
    return {
      json: {
        id: leadId,
        status: "error",
        error_details: error.message,
        raw_output: item.json.output
      }
    };
  }
});

