const items = $input.all();

// 2. Process every item in the list
return items.map(item => {
  const html = item.json.data || "";

  // 3. Regular Expression to find emails
  const emailRegex = /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9._-]+)/gi;
  const foundEmails = html.match(emailRegex);

  // 4. Remove duplicates and filter out image files
  const cleanEmails = foundEmails 
    ? [...new Set(foundEmails)].filter(e => !e.match(/\.(png|jpg|jpeg|gif|svg|webp)$/i))
    : [];

  // 5. Return the full lead data + the new email fields for each lead
  return {
    json: {
      ...item.json, 
      found_email: cleanEmails.length > 0 ? cleanEmails[0] : "No email found",
      all_emails: cleanEmails,
      html_content: html.substring(0, 5000) 
    }
  };
});

