// Node: ParseHTML
// Purpose: Clean HTML and extract ALL unique emails before sending to LLM

const items = $input.all();
return items.map(item => {
  const html = item.json.data || "";

  // 1. Clean the text for the LLM
  let text = html.replace(/<script\b[^>]*>([\s\S]*?)<\/script>/gm, "");
  text = text.replace(/<style\b[^>]*>([\s\S]*?)<\/style>/gm, "");
  text = text.replace(/<[^>]+>/g, " "); 
  text = text.replace(/\s+/g, " ").trim();

  // 2. Extract ALL Emails using Regex
  const emailRegex = /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9._-]+)/gi;
  const foundEmails = html.match(emailRegex);

  // 3. Remove duplicates and filter out image-based "fake" emails (e.g., info@site.com.png)
  const uniqueEmails = foundEmails 
    ? [...new Set(foundEmails)].filter(e => !e.match(/\.(png|jpg|jpeg|gif|svg|webp)$/i))
    : [];

  // 4. Output for LLM and Database
  return {
    json: {
      ...item.json, // Keeps your original lead ID and data
      clean_text: text.substring(0, 4000), 
      all_scraped_emails: uniqueEmails,
      primary_email: uniqueEmails.length > 0 ? uniqueEmails[0] : "No email found"
    }
  };
});

