// n8n Function node: Extract clean blog prompt text from AI response

// 1️⃣ Loop through input items
const output = [];

for (const item of items) {
  let rawText = '';

  // Check if content is stored inside item.json.content.parts
  const parts = item.json?.content?.parts;
  if (Array.isArray(parts)) {
    rawText = parts.map(p => p.text || '').join('\n');
  } else if (item.json?.text) {
    // Sometimes AI responses are directly in item.json.text
    rawText = item.json.text;
  }

  // 2️⃣ Extract text starting from "You are part of a team" onward
  const startIndex = rawText.indexOf('You are part of a team');
  let cleanText = rawText;

  if (startIndex !== -1) {
    cleanText = rawText.slice(startIndex).trim();
  }

  // 3️⃣ Remove unwanted characters, markdown or escape sequences
  cleanText = cleanText
    .replace(/\*\*/g, '') // remove bold markers
    .replace(/\r/g, '')
    .trim();

  // 4️⃣ Return only the clean parsed text
  output.push({
    json: {
      blog_prompt: cleanText,
    },
  });
}

return output;
