const raw = $json["raw_address"] || "";

if (!raw) {
  return [{ json: { street: "", city: "", postal_code: "", country: "" } }];
}

const parts = raw.split(",").map(p => p.trim());
const capitalize = str => str.replace(/\b\w/g, c => c.toUpperCase());

return [
  {
    json: {
      street: parts[0] ? capitalize(parts[0]) : "",
      city: parts[1] ? capitalize(parts[1]) : "",
      postal_code: parts[2] || "",
      country: parts[3] ? capitalize(parts[3]) : "",
    }
  }
];
