const principal = query.loan_amount;
const annualRate = query.annual_rate;
const years = query.term_years;

// Convert annual rate percentage to a monthly decimal rate
const monthlyRate = (annualRate / 100) / 12;
// Convert years to total number of payments
const numberOfPayments = years * 12;

if (principal <= 0 || annualRate <= 0 || years <= 0) {
  return { error: "Loan amount, interest rate, and term must be positive numbers." };
}

// Amortization formula: M = P * [r(1+r)^n] / [(1+r)^n - 1]
const monthlyPayment = principal * (monthlyRate * Math.pow(1 + monthlyRate, numberOfPayments)) / (Math.pow(1 + monthlyRate, numberOfPayments) - 1);

const result = {
  monthly_payment: monthlyPayment.toFixed(2), // Format to 2 decimal places for currency
  total_paid: (monthlyPayment * numberOfPayments).toFixed(2),
  total_interest: ((monthlyPayment * numberOfPayments) - principal).toFixed(2)
};

return JSON.stringify(result);
